def findall(p, s):
    '''Yields all the positions of
    the pattern p in the string s.'''
    i = s.find(p)
    while i != -1:
        yield i
        i = s.find(p, i+1)

def find_entities(quote, text, entities=ENTITIES):
    sentence_position = text.find(quote)
    if sentence_position == -1:
        return

    splited = text.split(quote)
    entities_found = {}
    closest_distance = 100000000

    closest_entity = ""
    for entity in entities:
        for chunk in splited:
            for distance in findall(entity, chunk):
                print("Found entity at " + str(distance))
                entities_found[entity] = distance
                if distance > -1 and abs(distance-sentence_position) < closest_distance:
                    closest_distance = abs(distance-sentence_position)
                    closest_entity = entity
    
    print("QUOTE: " + quote + " | TEXT: " + text + "| CLOSEST ENTITY: " + closest_entity + " | DISTANCE: " + str(closest_distance))

