    table = str.maketrans(dict.fromkeys(string.punctuation))  # OR {key: None for key in string.punctuation}
        
    for text in my_text:
        text = text.lower()
        text_clean = text.translate(table)   
        
        entities = []
        for entity in ENTITIES:
            entity = entity.lower()
            found = [e for e in findall(entity,text_clean)]
            if len(found) > 0:
                entities.append((entity, found))

        quotes_re = re.compile('[\"“]([^\"”]*)?[\n\"”]\s*(?:[\.\s,]|$)') #non greedy regex to find new quotes and stop at newlines
        quotes = [(q,text.find(q)) for q in re.findall(quotes_re,text)]
        
        verbs = []
        for verb in VERBS:
            found = [e for e in findall(verb,text_clean)]
            if len(found) > 0:
                verbs.append((verb,found))

        entities_found = []
        for verb in verbs:
            for verb_pos in verb[1]:
                for entity in entities:
                    for entity_pos in entity[1]:
                        if abs(verb_pos-entity_pos) < len(verb[0]) + len(entity[0]) + 10:
                            entities_found.append((entity[0], entity_pos))

        if(len(entities_found) > 0):
            print(text, "\n", entities_found)


